export interface BootstrapSelectOptions {
    noneSelectedText: string;
    noneResultsText: string;
    noneValue: string;
    countSelectedText(numSelected: number, _numTotal: number): "1 item selected" | `${number} items selected`;
    maxOptionsText(numAll: number, numGroup: number): string[];
    local: string;
    selectAllText: string;
    deselectAllText: string;
    doneButton: boolean;
    multipleSeparator: string;
    style: string;
    size: string;
    title: string;
    placeholder: string;
    allowClear: boolean;
    selectedTextFormat: string;
    width: string;
    header: boolean | string;
    search: boolean;
    searchPlaceholder: string;
    normalizeSearch: boolean;
    actionsBox: boolean;
    showTick: boolean;
    template: {
        dropdownButton: ($el: HTMLSelectElement) => string;
        dropdown: () => string;
        dropdownMenu: () => string;
        serchInput: () => string;
        header: () => string;
        item: ($el: HTMLOptionElement) => string;
        optgroup: ($el: HTMLOptGroupElement) => string;
        option: ($el: HTMLOptionElement, multiple: boolean) => string;
        divider: () => string;
        checkMark: () => string;
    };
    maxOptions: boolean;
    mobile: boolean;
    selectOnTab: boolean;
    dropdownPosition: string;
    virtualScroll: number;
}
